var div = document.getElementById("divs");
var cardSrc = document.getElementById("cardSrc");
var cardTitle = document.getElementById("cardTitle");
var cardDescription = document.getElementById("cardDescription");
var arr = []

function clk() {
    
    var obj = {
        img: cardSrc.value,
        title: cardTitle.value,
        p: cardDescription.value,
        // img: 'https://img.freepik.com/free-photo/3d-car-with-simple-background_23-2150796866.jpg?t=st=1718475878~exp=1718479478~hmac=4172f019ee293cc9c7d8ece6af7749a865c02c1fb7cd6053d82f169d3a84c4a4&w=740',
        // title: "Car", 
        // p: "Some quick example text to build on the card title and make up the bulk of the card's content.",
    }
    arr.push(obj);
    localStorage.setItem("data", JSON.stringify(arr));

    div.innerHTML += `<div class="card" style="width: 18rem;">
    <img src=${obj.img} class="card-img-top" alt="...">
    <div class="card-body">
    <h5 class="card-title">${obj.title}</h5>
    <p class="card-text">${obj.p}</p>
    </div>
    </div>`
}
function call() {
    var get = JSON.parse(localStorage.getItem("data"));
    get.map(function (data) {
        var div1 = document.createElement("div");
        div1.setAttribute("class", "card")
        var img1 = document.createElement("img");
        img1.src = data.img;
        div1.appendChild(img1)
        div.appendChild(div1);       

        var div2  = document.createElement("div");
        div2.setAttribute("class", "card-body")
        div1.appendChild(div2)

        var head = document.createElement("h5");
        head.setAttribute("class", "card-title");
        var headText = document.createTextNode(data.title);
        head.appendChild(headText);
        div2.appendChild(head);

        var para = document.createElement("p");
        para.setAttribute("class", "card-text");
        var paraText = document.createTextNode(data.p);
        para.appendChild(paraText);
        div2.appendChild(para);

        // var btn = document.createElement("button");
        // btn.setAttribute("class", "btn btn-primary");
        // var btnText = document.createTextNode("Go where");
        // btn.appendChild(btnText);
        // div2.appendChild(btn);

    })
    // console.log(get);
}
if(localStorage.getItem("data"))
    {
        call()
    }
    cardTitle.value = '';
    cardSrc.value = '';
    cardDescription.value = '';